.slds-button_brand-outline {
    border-color: rgb(239, 172, 6);
}
.slds-button_stateful .slds-button_stateful:hover, .slds-button_stateful .slds-text-selected-focus {
    background-color: #b0ffb0;
    color: forestgreen;
    border-color: forestgreen;
}
.slds-button_stateful .slds-text-selected {
    color: forestgreen;
}
.slds-button_stateful .slds-text-not-selected-focus {
    color: forestgreen;
    border-color: forestgreen;
}
.slds-button_neutral.slds-is-selected,
.slds-button--neutral.slds-is-selected {
  border-color: forestgreen;
  background-color: #b0ffb0; 
}
.slds-button_stateful .slds-text-selected-focus {
    color: red;
}
.course-name {
    color: #1e7891;
    font-size: 16px;
    font-weight: bold;
}
